import React, { Component } from 'react'
import {motion} from 'framer-motion'
import Card from "./Card"
import * as biIcons from "react-icons/bi" 
const line = "PROJECTS"
const sentence = {
    hidden: {opacity:1},
    visible : {
        opacity:1,
        transition: {
            delay:0.5,
            staggerChildren:0.2,
        },
    },
}
const letter = {
    hidden : { opacity:0,y:50},
    visible : {
        opacity:1,
        y:0,
    },

}
class Project extends Component {
    constructor(props) {
        super(props)

        this.state = {

        }
    }

    render() {
        return (
            <div className="education" >
                <motion.header variants={sentence} initial="hidden" animate="visible">
                {
                    line.split("").map((char,index) => {
                        return (
                            <motion.span key={char + "-" + index} variants={letter}>
                                {char}
                            </motion.span>
                        )
                    })
                }
                </motion.header>
            <div className="project">

            <div className="view-port" style={styles.view_port}>
            <div className="project-button-1">
                <button>
                    <biIcons.BiLeftArrow size={30} color="black"/>
                </button>
            </div>
                <div className="card-container" style={styles.card_container}>
                    <Card card_number="0"/>
                    <Card card_number="1"/>
                    <Card card_number="2"/>
                    <Card card_number="3"/>

                </div>
            </div>
            </div>
            </div>
        )
    }
}
const styles = {
    view_port : {
        //transform: 'translate(-50%, -50%)',
        display:'flex',
        justifyContent: 'center',
        width: '350px',
        height: '300px',
        backgroundColor: 'red',
        marginTop: '50px',
        // position: "absolute",
        // left: "50%",
        // top: "50%"
        overflow:"hidden",
        flexDirection: 'row'
    },
    card_container: {
        display: 'flex',
        flexDirection: 'row',
        width: 'fit-content'
    }
}
export default Project
